mapper.select:
select class_no as no, class_title as title, teacher_name as name, class_price as price, class_discountprice as discountPrice, class_image as image, category_no as categoryNo, category_name as categoryName, category_par_no as categoryParNo from ( select row_number() over (order by class_no desc) rn, A.* from (select distinct A.class_no, A.class_title, B.teacher_name, A.class_price, A.class_discountprice, A.class_image, C.category_no, C.category_name, C.category_par_no from final_class_tb A, final_teacher_tb B, final_category_tb C where A.teacher_no = B.teacher_no and A.category_no = C.category_no
start with C.category_no = #{category} connect by prior C.category_no = C.category_par_no
) A where class_title like '%' || #{value} || '%' OR teacher_name like '%' || #{value} || '%' ) where rn between #{beginIndex} and #{endIndex}

select class_no as no, class_title as title, class_content as content, class_difficultly as difficultly, class_image as image, class_video as video, class_income as income, class_period as period, class_price as price, class_discountprice as discountPrice, class_permission as permission, class_filter as filter, class_status as status, class_application_date as applicationDate, class_open_date as openDate, class_updated_date as updatedDate, class_deleted_date as deletedDate, class_total_count as totalCount, class_total_time as totalTime, class_isdeleted as isDeleted, teacher_no as teacherNo, teacher_name as teacherName, category_no as categoryNo, category_name as categoryName, category_par_no as categoryParNo from ( select row_number() over (order by class_no desc) rn, A.* from (select distinct A.class_no, A.class_title, A.class_content, A.class_difficultly, A.class_image, A.class_video, A.class_income, A.class_period, A.class_price, A.class_discountprice, A.class_permission, A.class_filter, A.class_status, A.class_application_date, A.class_open_date, A.class_updated_date, A.class_deleted_date, A.class_total_count, A.class_total_time, A.class_isdeleted, B.teacher_no, B.teacher_name, C.category_no, C.category_name, C.category_par_no from final_class_tb A, final_teacher_tb B, final_category_tb C where A.teacher_no = B.teacher_no and A.category_no = C.category_no
start with C.category_no = #{category} connect by prior C.category_no = C.category_par_no
) A where class_title like '%' || #{value} || '%' OR teacher_name like '%' || #{value} || '%' ) where rn between #{beginIndex} and #{endIndex}
